#define CONC(A, B) CONC2(A, B)

#define IFUNC_SEC __ifunc_sec
#define IFUNC_SEC_STR "__ifunc_sec"
#define START_SEC CONC(__start_, IFUNC_SEC)
#define STOP_SEC CONC(__stop_, IFUNC_SEC)

